package TypeCasting;

public class ExplicittypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double decNumber = 5.69;
		int intNumber;
		intNumber = (int)decNumber;
		System.out.println("double value : "+decNumber);
		System.out.println("int value after type casting : "+intNumber);
	}

}
